import ReactDOM from "react-dom"
import React, { useState } from 'react'
import "./index.css"



export const MyInput = () => {
  const [value, setValue] = useState('');

  const handleChange = (event) => {
    setValue(event.target.value);
  }

  const print_inner = (value) => {
alert(value)

  }

  return (
    <>
      <input
        type="text"
        value={value}
        onChange={handleChange} />

      <p>Value: {value}</p>
      <button onClick={() => print_inner(value)}>кнопка</button>
    </>
  )
}

const Comp = () => {

const [question, setQuesion] = useState('Question')
const [answer, setAnswer] = useState('Answer')
// const [material, setMateril] = useState('material')
const [state, setState] = useState('question')


  const click_next_question = () => {
    fetch('http://45.9.40.200:7777/question')
    .then(response => response.json())
    .then(json => { console.log(json.question)
    setQuesion(json.question)
    setAnswer(json.answer)
    // setAnswer(json.answer)
    // setMateril(json.material)
  })
  .catch(err => alert(err))
  }

  const change_state = () => {
    if (state === 'question'){
        state = 'answer'
    } 


  }

return <div>
<div className="main-window">{ state === "question" ? true: false }</div>
<div className="btn-next-question" onClick={() => click_next_question()} >След вопрос</div>
<div className="btn-answer" onClick={() => change_state()} >Узнать ответ!!</div>



</div>
}



const root = ReactDOM.createRoot(document.getElementById('root'))
root.render(<Comp/>)
